import React, { useState } from 'react';
import './../styles/Notaries.css';
import notariesData from '../json/Notaries.json';
import NotaryCard from '../components/NotaryCard';
import Menu from '../components/Menu';

const Notaries = () => {
    const [notaries, setNotaries] = useState(notariesData);
    const [showMenu, setShowMenu] = useState(false);
    const [editingNotaryId, setEditingNotaryId] = useState(null);

    const handleToggleMenu = (notaryId) => {
        setEditingNotaryId(notaryId);
        setShowMenu(true);
    };

    const handleSubmit = (formData) => {
        // Обновляем данные выбранного нотариуса
        const updatedNotaries = notaries.map(notary =>
            notary.id === formData.id ? formData : notary
        );
        setNotaries(updatedNotaries);
        setShowMenu(false);
    };

    const handleCancel = () => {
        setShowMenu(false);
    };

    return (
        <div className="notaries-page-container">
            <div className='notaries-page-header'>
                <h1 className="title">Список нотариусов:</h1>
            </div>
            <div className='notaries-page-body'>
                {notaries.map(notary => (
                    <NotaryCard
                        key={notary.id}
                        notary={notary}
                        onEdit={() => handleToggleMenu(notary.id)}
                    />
                ))}
            </div>
            {/* Меню для редактирования */}
            {showMenu && (
                <Menu
                    initialData={notaries.find(notary => notary.id === editingNotaryId)}
                    onSubmit={handleSubmit}
                    onCancel={handleCancel}
                />
            )}
        </div>
    );
};

export default Notaries;
