import React, { useState } from 'react';
import './../styles/NotaryCard.css';
import Menu from './Menu'; // Импортируем компонент Menu

const NotaryCard = ({ notary }) => {
  const [showPopup, setShowPopup] = useState(false);
  const [editingData, setEditingData] = useState({}); // Состояние для данных редактируемого объекта

  const handleShowPopup = () => {
    setShowPopup(true);
  };

  const handleClosePopup = () => {
    setShowPopup(false);
  };

  const handleEdit = () => {
    // Устанавливаем начальные данные редактируемого объекта
    setEditingData(notary);
    // Показываем меню
    setShowPopup(true);
  };

  const handleSubmit = (formData) => {
    // Обрабатываем данные формы
    console.log('Form data:', formData);
    // Дополнительные действия при отправке данных, например, обновление состояния компонента
    // Например, можно добавить обновление списка нотариусов
    // ...
    // Закрываем меню
    handleClosePopup();
  };

  return (
    <div className="notary-card">
      <img src={notary.img} alt={notary.name} />
      <p>{notary.name}</p>
      <button onClick={handleShowPopup}>Подробнее</button>
      <button onClick={handleEdit}>Редактировать</button> {/* Добавляем обработчик события для кнопки "Редактировать" */}
      {showPopup && (
        <div className="popup-overlay">
          <div className="popup">
            <h3>{notary.name}</h3>
            <img src={notary.img} alt={notary.name} />
            <p>Опыт работы: {notary.experience}</p>
            <p>Телефон: {notary['phone-number']}</p>
            <div className="action-buttons">
              {/* Настройка передачи данных редактируемого объекта в меню */}
              <Menu initialData={editingData} onSubmit={handleSubmit} onCancel={handleClosePopup} />
            </div>
            <button className="close-button" onClick={handleClosePopup}>Закрыть</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default NotaryCard;
