import React, { useState } from 'react';
import './../styles/Notaries.css';
import notariesData from '../json/Notaries.json'; // Импортируем данные
import NotaryCard from '../components/NotaryCard'; // Импортируем компонент NotaryCard
import Menu from '../components/Menu'; // Импортируем компонент Menu

const Notaries = () => {
    const [notaries, setNotaries] = useState(notariesData);
    const [showMenu, setShowMenu] = useState(false);

    const handleToggleMenu = () => {
        setShowMenu(!showMenu);
    };

    const handleAddNotary = (formData) => {
        // Добавляем нового нотариуса к существующему списку
        setNotaries([...notaries, formData]);
        // Закрываем меню после добавления нотариуса
        setShowMenu(false);
    };

    const handleEditNotary = (formData) => {
        // Находим индекс редактируемого нотариуса в списке
        const index = notaries.findIndex(item => item.id === formData.id);
        if (index !== -1) {
            // Обновляем список нотариусов, заменяя редактируемого нотариуса на его обновленную версию
            const updatedNotaries = [...notaries];
            updatedNotaries[index] = formData;
            setNotaries(updatedNotaries);
            // Закрываем меню после редактирования нотариуса
            setShowMenu(false);
        }
    };

    const handleCancel = () => {
        setShowMenu(false);
    };

    return (
        <div className="notaries-page-container">
            <div className='notaries-page-header'>
                <h1 className="title">Список нотариусов:</h1>
                <ul>
                    <li>
                        {/* Кнопка вызова меню */}
                        <button onClick={handleToggleMenu}> Добавить </button>
                        {/* Показываем меню, если showMenu === true */}
                        {showMenu && <Menu onSubmit={handleAddNotary} onCancel={handleCancel} />}
                    </li>
                </ul>
            </div>

            {/* Тело страницы */}
            <div className='notaries-page-body'>
                {notaries.map(notary => (
                    <NotaryCard key={notary.id} notary={notary} onEdit={handleEditNotary} />
                ))}
            </div>
        </div>
    );
};


export default Notaries;
