import React, { useState } from 'react';
import './../styles/NotaryCard.css';
import Menu from './Menu'; // Импортируем компонент Menu

const NotaryCard = ({ notary }) => {
  const [showDetailsPopup, setShowDetailsPopup] = useState(false);
  const [showEditMenu, setShowEditMenu] = useState(false);
  const [editingData, setEditingData] = useState({}); // Состояние для данных редактируемого объекта

  const handleShowDetailsPopup = () => {
    setShowDetailsPopup(true);
  };

  const handleCloseDetailsPopup = () => {
    setShowDetailsPopup(false);
  };

  const handleShowEditMenu = () => {
    // Устанавливаем начальные данные редактируемого объекта
    setEditingData(notary);
    // Показываем меню
    setShowEditMenu(true);
  };

  const handleCloseEditMenu = () => {
    setShowEditMenu(false);
  };

  const handleSubmit = (formData) => {
    // Обрабатываем данные формы
    console.log('Form data:', formData);
    // Закрываем меню
    handleCloseEditMenu();
  };

  const handleSubmitSuccess = (formData) => {
    // Обновляем состояние или выполняем другие действия после успешной отправки формы
    console.log('Form submitted successfully:', formData);
  };

  return (
    <div className="notary-card">
      <img src={notary.img} alt={notary.name} />
      <p>{notary.name}</p>
      <button onClick={handleShowDetailsPopup}>Подробнее</button>
      {showDetailsPopup && (
        <div className="popup-overlay">
          <div className="popup">
            <h3>{notary.name}</h3>
            <img src={notary.img} alt={notary.name} />
            <p>Опыт работы: {notary.experience}</p>
            <p>Телефон: {notary['phone-number']}</p>
            <div className="action-buttons">
              {/* Обработчик события для кнопки "Редактировать" */}
              <button onClick={handleShowEditMenu}>Редактировать</button>
              <button>Удалить</button>
            </div>
            <button className="close-button" onClick={handleCloseDetailsPopup}>Закрыть</button>
          </div>
        </div>
      )}
      {/* Меню для редактирования */}
      {showEditMenu && (
        <Menu initialData={editingData} onSubmit={handleSubmit} onCancel={handleCloseEditMenu} onSubmitSuccess={handleSubmitSuccess} />
      )}
    </div>
  );
};

export default NotaryCard;
